import { useEffect, useState } from 'react';
import './Detail.scss'
import { motion } from 'framer-motion'
import { Drawer, Row, Col, Tag, Image } from 'antd';
import DescriptionsItem from '../DescriptionsItem';
import { staggerOne, authFadeInUpVariants } from '../../../utils/motion'




const Detail = ({ isShow, setIsShow, itemData }) => {
    const [getContainer, setContainer] = useState(null)
    useEffect(() => {
        //解决 ant-drawer-body 内字体颜色不跟随主题的问题 
        //指定 Drawer 挂载到layout的节点上
        setContainer(document.querySelector('.layout'))
    })

    const onClose = () => {
        setIsShow();
    }

    return (
        <Drawer
            title={itemData.book_name}
            size='large'
            rootStyle={{ position: 'absolute' }}
            placement="right"
            getContainer={getContainer}
            onClose={onClose}
            open={isShow}
        >
            <motion.div variants={staggerOne} >
                <motion.div variants={authFadeInUpVariants} >
                    <Row>
                        <Col span={7}>
                            <Image
                                width={180}
                                className="book_img"
                                src={itemData.book_image}
                                placeholder={
                                    <Image
                                        preview={false}
                                        src={itemData.book_image}
                                        fallback="data:image/png;base64,"
                                    />
                                }
                            />
                        </Col>
                        <Col span={16}>
                            <Row>
                                <Col span={24}>
                                    <DescriptionsItem title="书名：" content={itemData.book_name} />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="作者：" content={itemData.book_auth} />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="出版社：" content={itemData.book_press} />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="ISBN：" content={itemData.book_number} />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="出版日期：" content={itemData.book_publication_date} />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="类型：" content={
                                        itemData.book_type && itemData.book_type.map((type) => {
                                            return (
                                                <Tag color="orange" key={type}>
                                                    {type}
                                                </Tag>
                                            );
                                        })
                                    }
                                    />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="总数量：" content={itemData.book_count} />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="剩余数量：" content={itemData.book_surplus_quantity} />
                                </Col>
                                <Col span={24}>
                                    <DescriptionsItem title="位置：" content={itemData.book_position} />
                                </Col>

                            </Row>
                        </Col>
                    </Row>
                </motion.div>
                <motion.div variants={authFadeInUpVariants} >
                    <Col span={24}>
                        <DescriptionsItem title="内容简介：" content={itemData.book_intro} />
                    </Col>
                </motion.div>
            </motion.div>
        </Drawer>
    )
}

export default Detail